/**
 * 发现模块请求方法集合
 * @param httpClient {Object}
 * @constructor
 */
var DiscoveryService = function(httpClient) {
  this.http = httpClient;
};

/**
 * @description 获取产品列表
 * @param {String}  type          活动类型 activity-活动；information-资讯； game-游戏；littleJInxiu-小金秀;guid-新手引导;dataReport-数据报告
 * @param {int}     currentPage   当前页
 * @param {int}     pageCount     每页多少条记录
 * @see http://tools.jinhui365.cn/#!/interface/detail?id=170
 */
DiscoveryService.prototype.$getDiscoverList = function(type, currentPage, pageSize) {
  type = type || 'information';
  currentPage = currentPage || 1;
  pageSize = pageSize || 100;

  var url = '/information';
  return this.http.$getJson(url, {
    type: type,
    currentPage: currentPage,
    pageSize: pageSize
  });
};


module.exports = exports = DiscoveryService;
