/**
 * @description 帮助相关请求方法集合
 * @param httpClient {Object}
 * @constructor
 */
var HelpService = function(httpClient) {
  this.http = httpClient;
};

/**
 * @description 获取帮助中心首页信息
 * @see http://tools.jinhui365.cn/#!/interface/detail?id=183
 */
HelpService.prototype.$getHelpCenter = function() {
  var url = '/helpCenter/home';
  return this.http.$getJson(url);
};

/**
 * @description 获取帮助中心某一类问题列表
 * @param {String} typeId  上一级分类的唯一标识id，无默认值，不可为空值
 * @see http://tools.jinhui365.cn/#!/interface/detail?id=184
 */
HelpService.prototype.$getCategories = function(typeId) {
  var url = '/helpCenter/categories';
  return this.http.$getJson(url, {
    typeId:typeId
  });
};

/**
 * @description 获取问题详情
 * @param {String} groupId  上级分类的id，无默认值且不可为空
 * @param {String} problemId  问题的唯一标识id，无默认值且不可为空
 * @see http://tools.jinhui365.cn/#!/interface/detail?id=185
 */
HelpService.prototype.$getIssueDetail = function(groupId, problemId) {
  var url = '/helpCenter/issueDetail';
  return this.http.$getJson(url, {
    groupId:groupId,
    problemId:problemId
  });
};

module.exports = exports = HelpService;
