/**
 * 保险查询相关请求方法集合
 * @param httpClient {Object}
 * @constructor
 */
var InsureService = function(httpClient) {
  this.http = httpClient;
};


/**
 * @description 获取保单列表
 * @param {String}  status          保单状态
 * @see http://tools.jinhui365.cn/#!/interface/detail?id=210
 */
InsureService.prototype.$getPolicyList = function(status) {
  status = status || 'all';

  var url = '/insurance/orderList';
  return this.http.$getJson(url, {
    status: status
  });
};

/**
 * @description 获取保单详情
 * @param {String} id 保单编号
 * @see http://tools.jinhui365.cn/#!/interface/detail?id=211
 */
InsureService.prototype.$getPolicyDetail = function(id) {
  var url = '/insurance/orderDetail';
  return this.http.$getJson(url, {
    id: id
  });
};

module.exports = exports = InsureService;