var MontaiServices = function(httpClient) {
  this.http = httpClient;
};
/*
 *@description  茅酒会产品详情接口
 *@see  http://tools.jinhui365.cn/interface/detail?id=462
 */
MontaiServices.prototype.$getMontaiDetail =function (code) {
  var url = '/api/product/detail/' + code
  return this.http.$getJson(url);
}

module.exports = exports = MontaiServices;
