/**
 * 工具接口结合
 * @param {Object} httpClient
 * @constructor
 */
var UtilService = function(httpClient) {
  this.http = httpClient;
};

/**
 * 获取服务器时间
 */
UtilService.prototype.$getCurrentTime = function() {
  var url = '/currentTime';
  return this.http.$getJson(url);
};

module.exports = exports = UtilService;