var HttpService = require('../index');// text/index.js文件中已经实例化过HttpService对象，这里直接取出实例化之后的defaultService属性
var testIndex = require('./index');
var discoveryService = HttpService.$getDefaultService().$getDiscoveryService();

describe('发现列表接口', function(done){
  this.timeout(5000);

  it('discoveryList interface(no type argument) should return a right code', function(){
    return discoveryService.$getDiscoverList().should.eventually.satisfy(function(obj){
      return obj.code == 0;
    }).notify(done);
  });

  it('discoveryList interface(with type information) should return a right code', function(){
    return discoveryService.$getDiscoverList('information').should.eventually.satisfy(function(obj){
      return obj.code == 0;
    }).notify(done);
  });
});



