var HttpService = require('../index');
var testIndex = require('./index');
var utilService = HttpService.$getDefaultService().$getUtilService();

describe('测试utilService', function(done){
  this.timeout(5000);

  it('获取服务器时间接口', function(){
    return utilService.$getCurrentTime().should.eventually.satisfy(function(obj){
      return obj.message.code == 0;
    }).notify(done);
  });
});